#!/bin/bash
echo "{ \"response\": [ { \"datetime\": $(grep ro\.build\.date\.utc $OUT/system/build.prop | cut -d= -f2), \"filename\": \"$(basename $(ls $OUT/lineage-17.1-*-UNOFFICIAL-mido.zip))\", \"id\": \"$((sha256sum $OUT/lineage-17.1-*-UNOFFICIAL-mido.zip) | cut -d ' ' -f1)\", \"romtype\": \"UNOFFICIAL\", \"size\": $(stat -c%s $OUT/lineage-17.1-*-UNOFFICIAL-mido.zip), \"url\": \"https://downloads.sourceforge.net/project/lineageos-17-1/mido/$(basename $(ls $OUT/lineage-17.1-*-UNOFFICIAL-mido.zip))\", \"version\": \"$(grep -m1 ro\.lineage\.build\.version $OUT/system/build.prop | cut -d= -f2)\"  }]}" | tee $OUT/ota.json
